import React, { Component } from 'react';
import './index.css';

class TodoList extends Component {
    constructor() {
        super();
        this.state = {
            userInput: '',
            items: []
        };
    }

    onChange(event) {
        this.setState({
            userInput: event.target.value
        });
    }

    AjoutTache(event) {
        event.preventDefault();
        this.setState({
            userInput: '',
            items: [...this.state.items, this.state.userInput]
        });
    }

    SupprimeTache(item) {
        // no event 
        // pass the item to indexOf
        const array = this.state.items;
        const index = array.indexOf(item);
        array.splice(index, 1);
        this.setState({
            items: array
        });
    }

    renderTodos() {
        return this.state.items.map((item) => {
            return (
                <div className="list-group-item"
                    key={item} > {item} | < button onClick={this.SupprimeTache.bind(this, item)} > X </button> </div >
            );
        });
    }

    render() {
        return (
            <div>
                <h1> Todolist React </h1> <
                    form className="form-row align-items-center" >
                    <
                        input value={this.state.userInput}
                        type="text"
                        placeholder="Entrez une tÃ¢che !"
                        onChange={this.onChange.bind(this)}
                        className="form-control mb-2" />
                    <input onClick={this.AjoutTache.bind(this)}
                        className="btn btn-primary"> Ajouter </input></form><div className="list-group" > {this.renderTodos()} </div>
            </div>
        );
    }
}

export default TodoList;
